INSERT INTO `refrain` VALUES (1,1,1,'Du hast mich erlöst, Halleluja!<br>In Dir bin ich frei, Halleluja!<br>Wir singen dein Lob, Halleluja!<br>Gepriesen bist du, Halleluja!',NULL),(7,3,1,'|: Frohlocket ihr Völker, <br>verkündet laut: <br>der Herr ist erstanden, <br>der Tod besiegt!:|',NULL),(8,4,1,'Halleluja, halleluja, <br>sag Dank dem auferstandenen Herrn, <br>Halleluja, halleluja, <br>preise seinen Namen.',NULL),(9,5,1,'Uns ist heute das Licht aufgestrahlt, <br>helles Licht in unserem Herrn. <br>Alle Grenzen werden zersprengt, <br>alle Herzen werden erhellt.',NULL),(10,6,1,'|:Halleluja, Halleluja, Halleluja, <br>Halleluja, Halleluja, Halleluja:|',NULL),(11,7,1,'|:Halleluja! Halleluja!:|',NULL),(12,8,1,'Preiset den Herrn, ihr Diener des Herrn, <br/>ihr alle, die ihr steht im Hause des Herrn. <br/>Von ganzer Kraft erhebet Seine Macht, <br/>verkündet dass Er lebt, der uns zu Zeugen macht!',NULL),(14,10,1,'Jerusalem, Jerusalem, <br/>leg’ Dein Gewand der Trauer ab! <br/>Jerusalem, Jerusalem, <br/>singe und tanze Deinem Gott!',NULL),(16,14,1,'|:Tanze, tanze Deinem Gott,<br> tanze Tochter Zions!:|',NULL),(18,16,1,'Meine Seele lobe Dich, o Herr! <br/>Dein Wort ist mir helles Licht. <br/>Auf allen Wegen leucht’ es mir. <br/>Dein Wort ist meine Kraft, <br/>ihm vertrau’ ich allein!',NULL),(19,21,1,'Heil’ger Geist, der uns zu Kindern macht, <br>zu Kinder Gottes dieser Welt. <br>Freiheit hast Du uns neu geschenkt, <br>lass uns Dich loben jeden Tag.',NULL),(20,22,1,'|:Lebe in mir, lebe in mir, lebe:|<br>Lebe in mir, lebe, Geist des Herrn.',NULL),(21,22,2,'|:Ström’ in mein Herz, <br>ström’ in mein Herz, ströme:|<br>Ström’ in mein Herz, <br>ströme, Geist des Herrn!',NULL),(22,22,3,'|:Brenne in mir, brenne in mir, brenne:|<br>Brenne in mir, brenne Geist des Herrn!',NULL),(23,35,1,'|: Jesus, Dein Licht, <br/>füll dies Land mit des Vaters Ehre! <br/>Komm, Heil’ger Geist, <br/>setz die Herzen in Brand! <br/>Fliess, Gnadenstrom, <br/>überflute dies Land mit Liebe! <br/>Sende dein Wort, <br/>Herr, Dein Licht strahle auf! :|',NULL),(24,36,1,'Atme in uns, Heiliger Geist, <br>brenne in uns, Heiliger Geist, <br>wirke in uns, Heiliger Geist, <br>Atem Gottes, komm!',NULL),(25,37,1,'|:Ruach, Ruach, Ruach.:|',NULL),(26,41,1,'|:Hallelu,- Halleluja, <br>Hallelu,- Halleluja, <br>Hallelu,- Halleluja, Halleluja!:|',NULL),(27,42,1,'Herrlich sei Dein Name, <br/>herrlich sei Dein Name, <br/>herrlich sei Dein Name in der Welt.',NULL),(34,52,1,'Willkommen im Land der Ruhe,<br>im Land der Ruhe 3x',NULL),(35,55,1,'Höchster, allmächtiger und guter Herr,<br/>Dein sind der Lobpreis, <br/>die Herrlichkeit und Ehr.',NULL),(36,56,1,'|: Ich will für Dich mein Loblied singen, Herr, <br/>wie gross bist Du, o mein Gott!<br/>Mein ganzes Herz soll danken meinem Gott. <br/>Nur Dir soll mein Loblied gehör’n! :|',NULL),(37,67,1,'Ave Maria gratia plena <br>Dominus tecum benedicta tu',NULL),(38,69,1,'Alle meine Quellen entspringen in Dir,<br>in Dir mein guter Gott. <br>Du bist das Wasser, das mich tränkt <br>und meine Sehnsucht stillt.',NULL),(39,70,1,'Dein Reich komme, o Herr, <br>erhebe Dich in Deiner Macht. <br>Dir sei Ehre und Ruhm und Majestät. <br>Deine Herrlichkeit ist hier.',NULL),(40,74,1,'Mutter Maria, mit Dir will ich gehn, <br>lass meine Hand nie mehr los. <br>So wie ein Kind ohne Angst und ganz frei, <br>geh ich den Weg nun mit Dir.<br>',NULL),(41,77,1,'Ich will Dir singen, o Maria, meine Mutter, <br>warum Dein Name mein Herz frohlocken lässt, <br>und der Gedanke Deiner wunderbaren Grösse<br> in meiner Seele keine Angst erwecken kann.',NULL),(42,78,1,'I:Hosanna, Hosanna, <br>Hosanna in der Höhe!:I',NULL),(43,64,1,'Gloria, gloria in excelsis Deo,<br>Gloria, gloria in excelsis Deo, in excelsis Deo!',NULL),(44,91,1,'O Gott ewige Dreifaltigkeit, <br>was konntest Du mir mehr geben, als Dich selber. <br>Gut, über allen Gütern <br>Schönheit über aller Schönheit.',NULL),(45,90,1,'Gloria! Gloria! in excelsis Deo!<br/>Gloria! Gloria! in excelsis Deo!<br/>Et in terra pax hominibus